var room = 1;
var date = 2;

function education_fields() {

    room++;
    date++;
    var objTo = document.getElementById('education_fields')
    var divtest = document.createElement("div");
    divtest.setAttribute("class", "form-group removeclass" + room);
    var rdiv = 'removeclass' + room;
    divtest.innerHTML = '<div class="row"><div class="col-sm-4 nopadding"><div class="form-group"> <input type="text" class="form-control" id="nama_plan" name="nama_plan[]" value="" placeholder="Nama Pekerjaan"></div></div><div class="col-sm-5 nopadding"><div class="form-group"><textarea class="form-control" rows="3" placeholder="Keterangan Pekerjaan" name="ket_plan" id="ket_plan[]" required></textarea></div></div><div class="col-sm-3 nopadding"><div class="form-group"><div class="input-group"><input type="text" class="form-control mydatepicker2" data-date-format="dd/mm/yyyy" name="tgl_plan" id="tgl_plan[]" placeholder="dd/mm/yyyy" autocomplete="off" required><div class="input-group-append"><span class="input-group-text"><i class="icon-calender"></i></span></div><div class="input-group-append"> <button class="btn btn-danger" type="button" onclick="remove_education_fields(' + room + ');"> <i class="fa fa-minus"></i> </button></div></div></div></div><div class="clear"></div></row>';


    objTo.appendChild(divtest)
    $(function () {
        $('.mydatepicker2').each(function () {
            $(this).datepicker({
                autoclose: true,
                todayHighlight: true,
                dateFormat: "dd/mm/yyyy",
                orientation: 'bottom'
            });
        });
    });
}

function remove_education_fields(rid) {
    $('.removeclass' + rid).remove();
}